mapper.select:
SELECT COUNT(*)
  FROM TB_COMPANY
 WHERE MANAGER_ID = #{managerId}

SELECT COUNT(*)
  FROM TB_COMPANY
 WHERE BUSINESS_NUM = #{businessNum}

mapper.insert:
 		INSERT INTO TB_COMPANY
 		(
			  COMPANY_NM
			, MANAGER_ID
			, MANAGER_PW
			, MANAGER_NM
			, PHONE
			, ADDR
			, TEL
			, EMAIL
			, KIND
			, INTRO
			, SIDO
			, SIGUNGU
			, DONG
			, LAT
			, LOGT
			, BUSINESS_NUM
			, CREATE_DATE
 		) 
 		VALUES
 		(
			  #{companyNm}
			, #{managerId}
			, #{managerPw}
			, #{managerNm}
			, #{phone}
			, #{addr}
			, #{tel} 
			, #{email}
			, #{kind}
			, #{intro}
			, #{sido}
			, #{sigungu}
			, #{dong}
			, #{lat}
			, #{logt}
			, #{businessNum} 
			, NOW()
 		)
