mapper.select:
SELECT COUNT(*)
  FROM TB_USER
 WHERE USER_ID = #{userId}

SELECT COUNT(*)
  FROM TB_USER
 WHERE USER_ID = #{userId}
   and USER_PW = #{userPw}

mapper.insert:
 		INSERT INTO TB_USER
 		(
			USER_ID
			, USER_PW
			, USER_NM
			, AGE
			, JOB
			, SEX
			, CAREER
			, PHONE
			, ADDR
			, EMAIL
			, INTRO
 		) 
 		VALUES
 		(
			#{userId}
			, #{userPw}
			, #{userNm}
			, #{age} 
			, #{job}
			, #{sex}
			, #{career}
			, #{phone}
			, #{email}
			, #{addr}
			, #{intro}
 		)
