mapper.select:
SELECT COUNT(*)
  FROM TB_USER
 WHERE USER_ID = #{userId}

SELECT COUNT(*)
  FROM TB_USER
 WHERE USER_ID = #{userId}
   and USER_PW = #{userPw}

mapper.insert:
 		INSERT INTO TB_USER
 		(
			USER_ID
			, USER_PW
			, USER_NM
			, AGE
			, JOB
			, SEX
			, CAREER
			, PHONE
			, ADDR
			, EMAIL
			, INTRO
			, SKILL
			, CREATE_DATE
 		) 
 		VALUES
 		(
			#{userId}
			, #{userPw}
			, #{userNm}
			, #{age} 
			, #{job}
			, #{sex}
			, #{career}
			, #{phone}
			, #{email}
			, #{addr}
			, #{intro}
			, #{skill}
			, NOW()
 		)

 		INSERT INTO TB_REQUIRE
		(    
			  REQ_NO
			, USER_NO
			, SALARY_HOUR
			, SALARY_DAY
			, SEARCH_TYPE
			, SEARCH_DATE
			, SEARCH_START
			, SEARCH_END
			, SEARCH_DOW
			, SEARCH_TIME
			, LOCATION
			, CREATE_DATE
		)
		VALUES
		(
			  #{userNo}
			, #{userNo}
			, #{salaryHour}
			, #{salaryDay}
			, #{searchType}
			, #{searchDate}
			, #{searchStart}
			, #{searchEnd}
			, #{searchDow}
			, #{searchTime}
			, #{location}
			, NOW()
		) 
		ON DUPLICATE KEY UPDATE
			  SALARY_HOUR = VALUES(SALARY_HOUR)
			, SALARY_DAY = VALUES(SALARY_DAY)
			, SEARCH_TYPE = VALUES(SEARCH_TYPE)
			, SEARCH_DATE = VALUES(SEARCH_DATE)
			, SEARCH_START = VALUES(SEARCH_START)
			, SEARCH_END = VALUES(SEARCH_END)
			, SEARCH_DOW = VALUES(SEARCH_DOW)
			, SEARCH_TIME = VALUES(SEARCH_TIME)
			, LOCATION = VALUES(LOCATION)
			, CREATE_DATE = NOW()
